  `asa_name` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `preview_begin_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  `preview_end_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
